import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Home, Calendar, ClipboardList, BarChart2, 
  Settings, Dumbbell, Ruler 
} from 'lucide-react';

const Navbar = () => {
  const location = useLocation();
  
  const navItems = [
    { path: '/', icon: Home, label: 'Home' },
    { path: '/planner', icon: Dumbbell, label: 'Treinos' },
    { path: '/calendar', icon: Calendar, label: 'Agenda' },
    { path: '/history', icon: ClipboardList, label: 'Histórico' },
    { path: '/progress', icon: BarChart2, label: 'Progresso' },
    { path: '/measurements', icon: Ruler, label: 'Medidas' },
    { path: '/settings', icon: Settings, label: 'Config' }
  ];
  
  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-gray-800 border-t border-gray-700 md:relative">
      <div className="flex justify-around py-2">
        {navItems.map((item) => {
          const Icon = item.icon;
          const isActive = location.pathname === item.path;
          
          return (
            <Link
              key={item.path}
              to={item.path}
              className={`flex flex-col items-center px-2 py-1 ${
                isActive ? 'text-blue-500' : 'text-gray-400 hover:text-gray-200'
              }`}
            >
              <Icon size={20} />
              <span className="text-xs mt-1">{item.label}</span>
            </Link>
          );
        })}
      </div>
    </nav>
  );
};

export default Navbar;
