import React, { useState } from 'react';
import { PlusIcon } from 'lucide-react';

const ExerciseForm = ({ onAddExercise }) => {
  const [isAdding, setIsAdding] = useState(false);
  const [exerciseName, setExerciseName] = useState('');
  const [sets, setSets] = useState(3);
  const [reps, setReps] = useState(10);
  const [weight, setWeight] = useState(0);
  const [restTime, setRestTime] = useState(60);
  const [notes, setNotes] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!exerciseName.trim()) {
      alert('Por favor, insira um nome para o exercício');
      return;
    }

    onAddExercise({
      name: exerciseName,
      sets,
      reps,
      weight,
      restTime,
      notes
    });

    // Resetar formulário
    setExerciseName('');
    setNotes('');
    setIsAdding(false);
  };

  if (!isAdding) {
    return (
      <button
        onClick={() => setIsAdding(true)}
        className="w-full bg-gray-700 hover:bg-gray-600 text-white px-4 py-3 rounded flex items-center justify-center"
      >
        <PlusIcon size={18} className="mr-1" /> Adicionar Exercício
      </button>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="bg-gray-700 rounded p-4">
      <div className="mb-3">
        <label className="block text-sm font-medium mb-1">Nome do Exercício</label>
        <input
          type="text"
          value={exerciseName}
          onChange={(e) => setExerciseName(e.target.value)}
          className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="ex: Supino, Agachamento, etc."
          required
        />
      </div>

      <div className="grid grid-cols-2 gap-3 mb-3">
        <div>
          <label className="block text-sm font-medium mb-1">Séries</label>
          <input
            type="number"
            value={sets}
            onChange={(e) => setSets(parseInt(e.target.value) || 0)}
            min="1"
            className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Repetições</label>
          <input
            type="number"
            value={reps}
            onChange={(e) => setReps(parseInt(e.target.value) || 0)}
            min="1"
            className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-3 mb-3">
        <div>
          <label className="block text-sm font-medium mb-1">Peso (kg)</label>
          <input
            type="number"
            value={weight}
            onChange={(e) => setWeight(parseFloat(e.target.value) || 0)}
            min="0"
            step="0.5"
            className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Tempo de Descanso (segundos)</label>
          <input
            type="number"
            value={restTime}
            onChange={(e) => setRestTime(parseInt(e.target.value) || 0)}
            min="0"
            className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      <div className="mb-3">
        <label className="block text-sm font-medium mb-1">Observações (Opcional)</label>
        <textarea
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          className="w-full bg-gray-600 border border-gray-500 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="2"
          placeholder="Quaisquer dicas ou informações adicionais"
        ></textarea>
      </div>

      <div className="flex justify-end space-x-2">
        <button
          type="button"
          onClick={() => setIsAdding(false)}
          className="px-3 py-1 text-sm text-gray-300 hover:text-white"
        >
          Cancelar
        </button>
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
        >
          Adicionar Exercício
        </button>
      </div>
    </form>
  );
};

export default ExerciseForm;
