import React, { useContext } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import { Check, X } from 'lucide-react';

const WorkoutCalendar = () => {
  const { schedule, workouts, updateSchedule } = useContext(WorkoutContext);
  
  const days = [
    { id: 'monday', label: 'Segunda-feira' },
    { id: 'tuesday', label: 'Terça-feira' },
    { id: 'wednesday', label: 'Quarta-feira' },
    { id: 'thursday', label: 'Quinta-feira' },
    { id: 'friday', label: 'Sexta-feira' },
    { id: 'saturday', label: 'Sábado' },
    { id: 'sunday', label: 'Domingo' }
  ];
  
  const handleToggleWorkout = (dayId, workoutId) => {
    const currentWorkouts = schedule[dayId] || [];
    const isWorkoutScheduled = currentWorkouts.includes(workoutId);
    
    const updatedWorkouts = isWorkoutScheduled
      ? currentWorkouts.filter(id => id !== workoutId)
      : [...currentWorkouts, workoutId];
    
    updateSchedule(dayId, updatedWorkouts);
  };
  
  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Agenda de Treinos</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-7 gap-4">
        {days.map(day => (
          <div key={day.id} className="bg-gray-800 rounded-lg p-4 border border-gray-700">
            <h2 className="text-lg font-medium mb-4">{day.label}</h2>
            
            <div className="space-y-2">
              {workouts.map(workout => {
                const isScheduled = schedule[day.id]?.includes(workout.id);
                
                return (
                  <div
                    key={workout.id}
                    className="flex items-center justify-between p-2 bg-gray-700 rounded"
                  >
                    <span className="text-sm">{workout.name}</span>
                    <button
                      onClick={() => handleToggleWorkout(day.id, workout.id)}
                      className={`p-1 rounded ${
                        isScheduled
                          ? 'bg-green-600 hover:bg-green-700'
                          : 'bg-gray-600 hover:bg-gray-700'
                      }`}
                    >
                      {isScheduled ? (
                        <Check size={16} className="text-white" />
                      ) : (
                        <X size={16} className="text-white" />
                      )}
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WorkoutCalendar;
