import React, { useState, useEffect } from 'react';

const BodyMeasurements = () => {
  const [measurements, setMeasurements] = useState({
    weight: '',
    height: '',
    chest: '',
    waist: '',
    hips: '',
    biceps: '',
    thighs: '',
    calves: ''
  });
  
  const [history, setHistory] = useState([]);
  
  useEffect(() => {
    const savedHistory = localStorage.getItem('bodyMeasurements');
    if (savedHistory) {
      setHistory(JSON.parse(savedHistory));
    }
  }, []);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMeasurements(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    
    const newMeasurement = {
      ...measurements,
      date: new Date().toISOString()
    };
    
    const updatedHistory = [...history, newMeasurement];
    setHistory(updatedHistory);
    localStorage.setItem('bodyMeasurements', JSON.stringify(updatedHistory));
    
    // Reset form
    setMeasurements({
      weight: '',
      height: '',
      chest: '',
      waist: '',
      hips: '',
      biceps: '',
      thighs: '',
      calves: ''
    });
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };
  
  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Medidas Corporais</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Formulário */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-lg font-medium mb-4">Registrar Medidas</h2>
          
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Peso (kg)
                </label>
                <input
                  type="number"
                  name="weight"
                  value={measurements.weight}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Altura (cm)
                </label>
                <input
                  type="number"
                  name="height"
                  value={measurements.height}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                />
              </div>
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Peitoral (cm)
                </label>
                <input
                  type="number"
                  name="chest"
                  value={measurements.chest}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Cintura (cm)
                </label>
                <input
                  type="number"
                  name="waist"
                  value={measurements.waist}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Quadril (cm)
                </label>
                <input
                  type="number"
                  name="hips"
                  value={measurements.hips}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Bíceps (cm)
                </label>
                <input
                  type="number"
                  name="biceps"
                  value={measurements.biceps}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Coxas (cm)
                </label>
                <input
                  type="number"
                  name="thighs"
                  value={measurements.thighs}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">
                  Panturrilhas (cm)
                </label>
                <input
                  type="number"
                  name="calves"
                  value={measurements.calves}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                  step="0.1"
                />
              </div>
            </div>
            
            <button
              type="submit"
              className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
            >
              Salvar Medidas
            </button>
          </form>
        </div>
        
        {/* Histórico */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-lg font-medium mb-4">Histórico de Medidas</h2>
          
          {history.length === 0 ? (
            <div className="text-center text-gray-400 py-6">
              <p>Nenhuma medida registrada ainda.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {history
                .sort((a, b) => new Date(b.date) - new Date(a.date))
                .map((measurement, index) => (
                  <div key={index} className="bg-gray-700 rounded p-3">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-medium">
                        {formatDate(measurement.date)}
                      </h3>
                    </div>
                    
                    <div className="grid grid-cols-2 gap-2 text-sm">
                      <div>
                        <span className="text-gray-400">Peso:</span>{' '}
                        {measurement.weight} kg
                      </div>
                      <div>
                        <span className="text-gray-400">Altura:</span>{' '}
                        {measurement.height} cm
                      </div>
                      <div>
                        <span className="text-gray-400">Peitoral:</span>{' '}
                        {measurement.chest} cm
