import React, { createContext, useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { saveWorkouts, getWorkouts, saveCompletedWorkouts, getCompletedWorkouts, saveSchedule, getSchedule } from '../services/storageService';

export const WorkoutContext = createContext();

export const WorkoutProvider = ({ children }) => {
  const [workouts, setWorkouts] = useState([]);
  const [completedWorkouts, setCompletedWorkouts] = useState([]);
  const [schedule, setSchedule] = useState({
    monday: [],
    tuesday: [],
    wednesday: [],
    thursday: [],
    friday: [],
    saturday: [],
    sunday: []
  });

  useEffect(() => {
    // Carrega dados do localStorage
    const loadedWorkouts = getWorkouts();
    const loadedCompletedWorkouts = getCompletedWorkouts();
    const loadedSchedule = getSchedule();

    if (loadedWorkouts) setWorkouts(loadedWorkouts);
    if (loadedCompletedWorkouts) setCompletedWorkouts(loadedCompletedWorkouts);
    if (loadedSchedule) setSchedule(loadedSchedule);
  }, []);

  const addWorkout = (workout) => {
    const newWorkout = {
      ...workout,
      id: uuidv4(),
      createdAt: new Date().toISOString()
    };
    
    const updatedWorkouts = [...workouts, newWorkout];
    setWorkouts(updatedWorkouts);
    saveWorkouts(updatedWorkouts);
  };

  const updateWorkout = (updatedWorkout) => {
    const updatedWorkouts = workouts.map(workout =>
      workout.id === updatedWorkout.id ? updatedWorkout : workout
    );
    
    setWorkouts(updatedWorkouts);
    saveWorkouts(updatedWorkouts);
  };

  const deleteWorkout = (workoutId) => {
    const updatedWorkouts = workouts.filter(workout => workout.id !== workoutId);
    setWorkouts(updatedWorkouts);
    saveWorkouts(updatedWorkouts);
  };

  const completeWorkout = (workoutId) => {
    const workout = workouts.find(w => w.id === workoutId);
    if (!workout) return;

    const completedWorkout = {
      ...workout,
      completedAt: new Date().toISOString()
    };

    const updatedCompletedWorkouts = [...completedWorkouts, completedWorkout];
    setCompletedWorkouts(updatedCompletedWorkouts);
    saveCompletedWorkouts(updatedCompletedWorkouts);
  };

  const updateSchedule = (day, workoutIds) => {
    const updatedSchedule = {
      ...schedule,
      [day]: workoutIds
    };
    
    setSchedule(updatedSchedule);
    saveSchedule(updatedSchedule);
  };

  const value = {
    workouts,
    completedWorkouts,
    schedule,
    addWorkout,
    updateWorkout,
    deleteWorkout,
    completeWorkout,
    updateSchedule
  };

  return (
    <WorkoutContext.Provider value={value}>
      {children}
    </WorkoutContext.Provider>
  );
};
