import React, { useContext } from 'react';
import { ThemeContext } from '../contexts/ThemeContext';
import { AuthContext } from '../contexts/AuthContext';

const Settings = () => {
  const { isDarkMode, toggleTheme } = useContext(ThemeContext);
  const { user, setUser } = useContext(AuthContext);
  
  const handleExportData = () => {
    const data = {
      workouts: JSON.parse(localStorage.getItem('workouts') || '[]'),
      completedWorkouts: JSON.parse(localStorage.getItem('completedWorkouts') || '[]'),
      schedule: JSON.parse(localStorage.getItem('schedule') || '{}'),
      bodyMeasurements: JSON.parse(localStorage.getItem('bodyMeasurements') || '[]')
    };
    
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'workout-tracker-data.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  
  const handleImportData = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const data = JSON.parse(event.target.result);
        
        if (data.workouts) localStorage.setItem('workouts', JSON.stringify(data.workouts));
        if (data.completedWorkouts) localStorage.setItem('completedWorkouts', JSON.stringify(data.completedWorkouts));
        if (data.schedule) localStorage.setItem('schedule', JSON.stringify(data.schedule));
        if (data.bodyMeasurements) localStorage.setItem('bodyMeasurements', JSON.stringify(data.bodyMeasurements));
        
        window.location.reload();
      } catch (error) {
        console.error('Erro ao importar dados:', error);
      }
    };
    reader.readAsText(file);
  };
  
  const handleClearData = () => {
    if (window.confirm('Tem certeza que deseja limpar todos os dados? Esta ação não pode ser desfeita.')) {
      localStorage.clear();
      window.location.reload();
    }
  };
  
  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Configurações</h1>
      
      <div className="space-y-6">
        {/* Aparência */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-lg font-medium mb-4">Aparência</h2>
          
          <div className="flex items-center justify-between">
            <span>Tema Escuro</span>
            <button
              onClick={toggleTheme}
              className={`px-4 py-2 rounded ${
                isDarkMode
                  ? 'bg-blue-600 hover:bg-blue-700'
                  : 'bg-gray-600 hover:bg-gray-700'
              } text-white`}
            >
              {isDarkMode ? 'Ativado' : 'Desativado'}
            </button>
          </div>
        </div>
        
        {/* Dados */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-lg font-medium mb-4">Gerenciar Dados</h2>
          
          <div className="space-y-4">
            <button
              onClick={handleExportData}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
            >
              Exportar Dados
            </button>
            
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Importar Dados
              </label>
              <input
                type="file"
                accept=".json"
                onChange={handleImportData}
                className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
              />
            </div>
            
            <button
              onClick={handleClearData}
              className="w-full bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
            >
              Limpar Todos os Dados
            </button>
          </div>
        </div>
        
        {/* Sobre */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-lg font-medium mb-4">Sobre</h2>
          
          <div className="space-y-2 text-gray-400">
            <p>Versão: 1.0.0</p>
            <p>Desenvolvido com React e Tailwind CSS</p>
            <p>© 2024 Workout Tracker</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
