import React, { useContext, useState } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import { CheckIcon, CalendarIcon, XIcon, PlusIcon } from 'lucide-react';

const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
const DAY_LABELS = ['Segunda', 'Terça', 'Quarta', 'Quinta', 'Sexta', 'Sábado', 'Domingo'];

const WorkoutCalendar = () => {
  const { 
    workouts, 
    schedule, 
    scheduleWorkout, 
    unscheduleWorkout, 
    markWorkoutComplete 
  } = useContext(WorkoutContext);
  
  const [selectedDay, setSelectedDay] = useState(null);
  const [isSelectingWorkout, setIsSelectingWorkout] = useState(false);

  const handleAddWorkout = (day) => {
    setSelectedDay(day);
    setIsSelectingWorkout(true);
  };

  const handleSelectWorkout = (workoutId) => {
    scheduleWorkout(selectedDay, workoutId);
    setIsSelectingWorkout(false);
    setSelectedDay(null);
  };

  const handleRemoveWorkout = (day, workoutId) => {
    unscheduleWorkout(day, workoutId);
  };

  const handleCompleteWorkout = (workoutId) => {
    markWorkoutComplete(workoutId);
  };

  // Obtém o dia da semana de hoje (0 = Domingo, 1 = Segunda, etc.)
  const today = new Date().getDay();
  // Converte para nosso formato (0 = Segunda, 6 = Domingo)
  const todayIndex = today === 0 ? 6 : today - 1;
  const todayName = DAYS[todayIndex];

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Agenda Semanal</h1>
        <div className="text-sm text-gray-400">
          <CalendarIcon className="inline mr-1" size={16} />
          Hoje é {DAY_LABELS[todayIndex]}
        </div>
      </div>

      {isSelectingWorkout ? (
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg">Selecione treino para {DAY_LABELS[DAYS.indexOf(selectedDay)]}</h3>
            <button
              onClick={() => {
                setIsSelectingWorkout(false);
                setSelectedDay(null);
              }}
              className="text-gray-400 hover:text-white"
            >
              <XIcon size={18} />
            </button>
          </div>

          {workouts.length === 0 ? (
            <div className="text-center py-6 text-gray-400">
              Você ainda não criou nenhum treino.
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-3">
              {workouts.map((workout) => (
                <button
                  key={workout.id}
                  onClick={() => handleSelectWorkout(workout.id)}
                  className="bg-gray-700 p-3 rounded-lg hover:bg-gray-600 text-left flex items-center"
                >
                  <div className="flex-1">
                    <h4 className="font-medium">{workout.name}</h4>
                    <p className="text-sm text-gray-400">
                      {workout.exercises.length} exercícios
                    </p>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      ) : (
        <div className="bg-gray-800 rounded-lg overflow-hidden">
          {DAYS.map((day, index) => {
            const isToday = day === todayName;
            const dayWorkouts = schedule[day] || [];
            
            return (
              <div 
                key={day} 
                className={`p-4 border-b border-gray-700 ${isToday ? 'bg-gray-700' : ''} ${index === DAYS.length - 1 ? 'border-none' : ''}`}
              >
                <div className="flex justify-between items-center mb-2">
                  <h3 className={`font-medium ${isToday ? 'text-white' : 'text-gray-300'}`}>
                    {DAY_LABELS[index]}
                    {isToday && <span className="ml-2 text-xs bg-blue-600 px-2 py-1 rounded-full">Hoje</span>}
                  </h3>
                  <button
                    onClick={() => handleAddWorkout(day)}
                    className="text-sm px-2 py-1 bg-gray-700 rounded flex items-center hover:bg-gray-600"
                  >
                    <PlusIcon size={16} className="mr-1" /> Adicionar
                  </button>
                </div>
                
                {dayWorkouts.length === 0 ? (
                  <div className="text-sm text-gray-500 italic py-2">
                    Nenhum treino agendado
                  </div>
                ) : (
                  <div className="space-y-2">
                    {dayWorkouts.map((workoutId) => {
                      const workout = workouts.find(w => w.id === workoutId);
                      if (!workout) return null;
                      
                      return (
                        <div key={workoutId} className="bg-gray-700 p-3 rounded-lg flex items-center">
                          <div className="flex-1">
                            <h4 className="font-medium">{workout.name}</h4>
                            <p className="text-xs text-gray-400">
                              {workout.exercises.length} exercícios
                            </p>
                          </div>
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleCompleteWorkout(workoutId)}
                              className="p-1 bg-green-600 rounded-full hover:bg-green-700"
                              title="Marcar como completo"
                            >
                              <CheckIcon size={16} />
                            </button>
                            <button
                              onClick={() => handleRemoveWorkout(day, workoutId)}
                              className="p-1 bg-red-600 rounded-full hover:bg-red-700"
                              title="Remover treino"
                            >
                              <XIcon size={16} />
                            </button>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default WorkoutCalendar;
