import React, { useState, useContext } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import { v4 as uuidv4 } from 'uuid';
import { PlusIcon, TrashIcon, PencilIcon, SaveIcon } from 'lucide-react';
import ExerciseForm from '../components/ExerciseForm';
import TimerModal from '../components/TimerModal';
import { motion, AnimatePresence } from 'framer-motion';
import { Plus, Trash2, Edit2, X } from 'lucide-react';

const WorkoutPlanner = () => {
  const { 
    workouts, 
    addWorkout, 
    updateWorkout, 
    deleteWorkout, 
    selectedWorkout, 
    setSelectedWorkout 
  } = useContext(WorkoutContext);

  const [showForm, setShowForm] = useState(false);
  const [showTimer, setShowTimer] = useState(false);
  const [timerDuration, setTimerDuration] = useState(60);
  const [workoutName, setWorkoutName] = useState('');
  const [exercises, setExercises] = useState([]);
  const [notes, setNotes] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [isCreating, setIsCreating] = useState(false);

  // Reseta formulário
  const resetForm = () => {
    setWorkoutName('');
    setExercises([]);
    setNotes('');
    setEditMode(false);
    setSelectedWorkout(null);
  };

  // Carrega treino para edição
  const handleEditWorkout = (workout) => {
    setSelectedWorkout(workout);
    setWorkoutName(workout.name);
    setExercises(workout.exercises);
    setNotes(workout.notes || '');
    setEditMode(true);
    setShowForm(true);
  };

  // Adiciona ou atualiza treino
  const handleSaveWorkout = () => {
    if (!workoutName.trim()) {
      alert('Por favor, insira um nome para o treino');
      return;
    }

    if (exercises.length === 0) {
      alert('Por favor, adicione pelo menos um exercício');
      return;
    }

    const workoutData = {
      name: workoutName,
      exercises,
      notes,
      updatedAt: new Date().toISOString()
    };

    if (editMode && selectedWorkout) {
      updateWorkout(selectedWorkout.id, workoutData);
    } else {
      addWorkout(workoutData);
    }

    resetForm();
    setShowForm(false);
  };

  // Adiciona exercício
  const handleAddExercise = (exercise) => {
    const newExercise = {
      id: uuidv4(),
      ...exercise
    };
    setExercises([...exercises, newExercise]);
  };

  // Remove exercício
  const handleRemoveExercise = (exerciseId) => {
    setExercises(exercises.filter(ex => ex.id !== exerciseId));
  };

  // Inicia temporizador de descanso
  const startTimer = (seconds) => {
    setTimerDuration(seconds);
    setShowTimer(true);
  };

  const handleCreateWorkout = () => {
    if (workoutName.trim()) {
      const newWorkout = {
        id: Date.now().toString(),
        name: workoutName,
        exercises: []
      };
      addWorkout(newWorkout);
      setSelectedWorkout(newWorkout);
      setIsCreating(false);
      setWorkoutName('');
    }
  };

  const handleDeleteExercise = (exerciseIndex) => {
    if (selectedWorkout) {
      const updatedExercises = [...selectedWorkout.exercises];
      updatedExercises.splice(exerciseIndex, 1);
      const updatedWorkout = {
        ...selectedWorkout,
        exercises: updatedExercises
      };
      updateWorkout(updatedWorkout);
      setSelectedWorkout(updatedWorkout);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4 pb-20">
      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        className="max-w-screen-xl mx-auto space-y-6"
      >
        <motion.div variants={itemVariants} className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-display font-bold text-gray-900 dark:text-white">
              Meus Treinos
            </h2>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setIsCreating(true)}
              className="flex items-center px-4 py-2 bg-primary-500 text-white rounded-lg hover:bg-primary-600 transition-colors"
            >
              <Plus className="w-4 h-4 mr-2" />
              Novo Treino
            </motion.button>
          </div>

          <AnimatePresence>
            {isCreating && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="mb-6"
              >
                <div className="flex gap-4">
                  <input
                    type="text"
                    value={workoutName}
                    onChange={(e) => setWorkoutName(e.target.value)}
                    placeholder="Nome do treino"
                    className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleCreateWorkout}
                    className="px-4 py-2 bg-primary-500 text-white rounded-lg hover:bg-primary-600 transition-colors"
                  >
                    Criar
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => {
                      setIsCreating(false);
                      setWorkoutName('');
                    }}
                    className="px-4 py-2 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-lg hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors"
                  >
                    <X className="w-5 h-5" />
                  </motion.button>
                </div>
              </motion.div>
            )}
          </AnimatePresence>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {workouts.map(workout => (
              <motion.div
                key={workout.id}
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
                className={`
                  p-4 rounded-lg cursor-pointer transition-colors
                  ${selectedWorkout?.id === workout.id
                    ? 'bg-primary-50 dark:bg-primary-900/20 border-2 border-primary-500'
                    : 'bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600'
                  }
                `}
                onClick={() => setSelectedWorkout(workout)}
              >
                <div className="flex justify-between items-start mb-2">
                  <h3 className="font-semibold text-gray-900 dark:text-white">
                    {workout.name}
                  </h3>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteWorkout(workout.id);
                      if (selectedWorkout?.id === workout.id) {
                        setSelectedWorkout(null);
                      }
                    }}
                    className="text-gray-500 hover:text-red-500 dark:text-gray-400 dark:hover:text-red-400"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {workout.exercises.length} exercícios
                </p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {selectedWorkout && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6"
          >
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-display font-bold text-gray-900 dark:text-white">
                {selectedWorkout.name}
              </h3>
              <div className="flex gap-2">
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="p-2 text-gray-500 hover:text-primary-500 dark:text-gray-400 dark:hover:text-primary-400"
                >
                  <Edit2 className="w-5 h-5" />
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => deleteWorkout(selectedWorkout.id)}
                  className="p-2 text-gray-500 hover:text-red-500 dark:text-gray-400 dark:hover:text-red-400"
                >
                  <Trash2 className="w-5 h-5" />
                </motion.button>
              </div>
            </div>

            <ExerciseForm onAddExercise={handleAddExercise} />

            <div className="mt-6 space-y-4">
              {selectedWorkout.exercises.map((exercise, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                  className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                >
                  <div>
                    <h4 className="font-medium text-gray-900 dark:text-white">
                      {exercise.name}
                    </h4>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {exercise.sets} séries x {exercise.reps} repetições
                      {exercise.weight && ` - ${exercise.weight}kg`}
                      {exercise.restTime && ` - ${exercise.restTime}s descanso`}
                    </p>
                  </div>
                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => handleDeleteExercise(index)}
                    className="p-2 text-gray-500 hover:text-red-500 dark:text-gray-400 dark:hover:text-red-400"
                  >
                    <Trash2 className="w-4 h-4" />
                  </motion.button>
                </motion.div>
              ))}
            </div>
          </motion.div>
        )}
      </motion.div>

      {showTimer && (
        <TimerModal
          duration={timerDuration}
          onClose={() => setShowTimer(false)}
        />
      )}
    </div>
  );
};

export default WorkoutPlanner;
