import React, { useState, useContext } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import ExerciseForm from '../components/ExerciseForm';
import { Plus, Trash2 } from 'lucide-react';

const WorkoutPlanner = () => {
  const { workouts, addWorkout, updateWorkout, deleteWorkout } = useContext(WorkoutContext);
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [workoutName, setWorkoutName] = useState('');
  
  const handleCreateWorkout = () => {
    if (!workoutName.trim()) return;
    
    const newWorkout = {
      name: workoutName,
      exercises: []
    };
    
    addWorkout(newWorkout);
    setWorkoutName('');
    setIsCreating(false);
  };
  
  const handleAddExercise = (exercise) => {
    if (!selectedWorkout) return;
    
    const updatedWorkout = {
      ...selectedWorkout,
      exercises: [...selectedWorkout.exercises, exercise]
    };
    
    updateWorkout(updatedWorkout);
    setSelectedWorkout(updatedWorkout);
  };
  
  const handleDeleteExercise = (exerciseIndex) => {
    if (!selectedWorkout) return;
    
    const updatedWorkout = {
      ...selectedWorkout,
      exercises: selectedWorkout.exercises.filter((_, index) => index !== exerciseIndex)
    };
    
    updateWorkout(updatedWorkout);
    setSelectedWorkout(updatedWorkout);
  };
  
  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Planejador de Treinos</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Lista de Treinos */}
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h2 className="text-xl mb-4">Meus Treinos</h2>
          
          {isCreating ? (
            <div className="mb-4">
              <input
                type="text"
                value={workoutName}
                onChange={(e) => setWorkoutName(e.target.value)}
                placeholder="Nome do treino"
                className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white mb-2"
              />
              <div className="flex space-x-2">
                <button
                  onClick={handleCreateWorkout}
                  className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded"
                >
                  Criar
                </button>
                <button
                  onClick={() => setIsCreating(false)}
                  className="bg-gray-600 hover:bg-gray-700 text-white px-3 py-1 rounded"
                >
                  Cancelar
                </button>
              </div>
            </div>
          ) : (
            <button
              onClick={() => setIsCreating(true)}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded mb-4 flex items-center justify-center"
            >
              <Plus size={20} className="mr-2" />
              Novo Treino
            </button>
          )}
          
          <div className="space-y-2">
            {workouts.map(workout => (
              <div
                key={workout.id}
                className={`p-3 rounded cursor-pointer ${
                  selectedWorkout?.id === workout.id
                    ? 'bg-blue-600'
                    : 'bg-gray-700 hover:bg-gray-600'
                }`}
                onClick={() => setSelectedWorkout(workout)}
              >
                <div className="font-medium">{workout.name}</div>
                <div className="text-sm text-gray-400">
                  {workout.exercises.length} exercícios
                </div>
              </div>
            ))}
          </div>
        </div>
        
        {/* Detalhes do Treino */}
        <div className="md:col-span-2">
          {selectedWorkout ? (
            <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl">{selectedWorkout.name}</h2>
                <button
                  onClick={() => deleteWorkout(selectedWorkout.id)}
                  className="text-red-500 hover:text-red-400"
                >
                  <Trash2 size={20} />
                </button>
              </div>
              
              <ExerciseForm onAddExercise={handleAddExercise} />
              
              <div className="mt-6 space-y-3">
                {selectedWorkout.exercises.map((exercise, index) => (
                  <div key={index} className="bg-gray-700 rounded p-3">
                    <div className="flex justify-between items-start">
                      <div>
                        <div className="font-medium">{exercise.name}</div>
                        <div className="text-sm text-gray-400">
                          {exercise.sets} séries x {exercise.reps} repetições
                          {exercise.weight && ` - ${exercise.weight}kg`}
                        </div>
                        <div className="text-sm text-gray-400">
                          Descanso: {exercise.restTime}s
                        </div>
                      </div>
                      <button
                        onClick={() => handleDeleteExercise(index)}
                        className="text-red-500 hover:text-red-400"
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="bg-gray-800 rounded-lg p-4 border border-gray-700 text-center text-gray-400">
              <p>Selecione um treino ou crie um novo para começar</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WorkoutPlanner;
