import React, { useContext } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const { schedule, workouts, completedWorkouts } = useContext(WorkoutContext);
  
  // Obtém o dia da semana atual (0 = Domingo, 1 = Segunda, etc.)
  const today = new Date().getDay();
  // Converte para nosso formato (0 = Segunda, 6 = Domingo)
  const todayIndex = today === 0 ? 6 : today - 1;
  const days = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
  const todayName = days[todayIndex];
  
  // Treinos de hoje
  const todaysWorkoutIds = schedule[todayName] || [];
  const todaysWorkouts = workouts.filter(workout => todaysWorkoutIds.includes(workout.id));
  
  // Estatísticas
  const totalWorkoutsCompleted = completedWorkouts.length;
  const lastWeekWorkouts = completedWorkouts.filter(workout => {
    const completedDate = new Date(workout.completedAt);
    const weekAgo = new Date();
    weekAgo.setDate(weekAgo.getDate() - 7);
    return completedDate >= weekAgo;
  }).length;
  
  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Dashboard</h1>
      
      <div className="bg-gray-800 rounded-lg p-4 border border-gray-700 mb-6">
        <h2 className="text-xl mb-4">Treinos de Hoje</h2>
        
        {todaysWorkouts.length === 0 ? (
          <div className="text-center py-6 text-gray-400">
            <p>Nenhum treino agendado para hoje.</p>
            <Link to="/calendar" className="text-blue-500 hover:text-blue-400 mt-2 inline-block">
              Agende um treino
            </Link>
          </div>
        ) : (
          <div className="space-y-3">
            {todaysWorkouts.map(workout => (
              <div key={workout.id} className="bg-gray-700 rounded p-3">
                <div className="font-medium">{workout.name}</div>
                <div className="text-sm text-gray-400">
                  {workout.exercises.length} exercícios
                </div>
                <button
                  className="mt-2 bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm"
                >
                  Iniciar Treino
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h3 className="text-lg mb-2">Estatísticas</h3>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-gray-700 rounded p-3 text-center">
              <div className="text-2xl font-bold">{totalWorkoutsCompleted}</div>
              <div className="text-sm text-gray-400">Total de Treinos</div>
            </div>
            <div className="bg-gray-700 rounded p-3 text-center">
              <div className="text-2xl font-bold">{lastWeekWorkouts}</div>
              <div className="text-sm text-gray-400">Últimos 7 dias</div>
            </div>
          </div>
        </div>
        
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <h3 className="text-lg mb-2">Progresso Recente</h3>
          <div className="text-center py-6 text-gray-400">
            <p>Seus dados de progresso aparecerão aqui</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
