import React, { useState, useContext } from 'react';
import { WorkoutContext } from '../contexts/WorkoutContext';
import { v4 as uuidv4 } from 'uuid';
import { PlusIcon, TrashIcon, PencilIcon, SaveIcon } from 'lucide-react';
import ExerciseForm from '../components/ExerciseForm';
import TimerModal from '../components/TimerModal';

const WorkoutPlanner = () => {
  const { 
    workouts, 
    addWorkout, 
    updateWorkout, 
    deleteWorkout, 
    selectedWorkout, 
    setSelectedWorkout 
  } = useContext(WorkoutContext);

  const [showForm, setShowForm] = useState(false);
  const [showTimer, setShowTimer] = useState(false);
  const [timerDuration, setTimerDuration] = useState(60);
  const [workoutName, setWorkoutName] = useState('');
  const [exercises, setExercises] = useState([]);
  const [notes, setNotes] = useState('');
  const [editMode, setEditMode] = useState(false);

  // Reseta formulário
  const resetForm = () => {
    setWorkoutName('');
    setExercises([]);
    setNotes('');
    setEditMode(false);
    setSelectedWorkout(null);
  };

  // Carrega treino para edição
  const handleEditWorkout = (workout) => {
    setSelectedWorkout(workout);
    setWorkoutName(workout.name);
    setExercises(workout.exercises);
    setNotes(workout.notes || '');
    setEditMode(true);
    setShowForm(true);
  };

  // Adiciona ou atualiza treino
  const handleSaveWorkout = () => {
    if (!workoutName.trim()) {
      alert('Por favor, insira um nome para o treino');
      return;
    }

    if (exercises.length === 0) {
      alert('Por favor, adicione pelo menos um exercício');
      return;
    }

    const workoutData = {
      name: workoutName,
      exercises,
      notes,
      updatedAt: new Date().toISOString()
    };

    if (editMode && selectedWorkout) {
      updateWorkout(selectedWorkout.id, workoutData);
    } else {
      addWorkout(workoutData);
    }

    resetForm();
    setShowForm(false);
  };

  // Adiciona exercício
  const handleAddExercise = (exercise) => {
    const newExercise = {
      id: uuidv4(),
      ...exercise
    };
    setExercises([...exercises, newExercise]);
  };

  // Remove exercício
  const handleRemoveExercise = (exerciseId) => {
    setExercises(exercises.filter(ex => ex.id !== exerciseId));
  };

  // Inicia temporizador de descanso
  const startTimer = (seconds) => {
    setTimerDuration(seconds);
    setShowTimer(true);
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-2xl font-bold mb-6">Planejador de Treinos</h1>

      {!showForm ? (
        <>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl">Meus Treinos</h2>
            <button
              onClick={() => setShowForm(true)}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center"
            >
              <PlusIcon size={18} className="mr-1" /> Novo Treino
            </button>
          </div>

          {workouts.length === 0 ? (
            <div className="text-center py-10 text-gray-400">
              <p>Você ainda não criou nenhum treino.</p>
              <button
                onClick={() => setShowForm(true)}
                className="text-blue-500 hover:text-blue-400 mt-2"
              >
                Crie seu primeiro treino
              </button>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {workouts.map(workout => (
                <div 
                  key={workout.id} 
                  className="bg-gray-800 rounded-lg p-4 border border-gray-700"
                >
                  <div className="flex justify-between items-start mb-2">
                    <h3 className="text-lg font-semibold">{workout.name}</h3>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEditWorkout(workout)}
                        className="text-blue-500 hover:text-blue-400"
                      >
                        <PencilIcon size={18} />
                      </button>
                      <button
                        onClick={() => {
                          if (window.confirm(`Excluir "${workout.name}"?`)) {
                            deleteWorkout(workout.id);
                          }
                        }}
                        className="text-red-500 hover:text-red-400"
                      >
                        <TrashIcon size={18} />
                      </button>
                    </div>
                  </div>

                  <div className="text-sm text-gray-400 mb-3">
                    {workout.exercises.length} exercícios
                  </div>

                  <div className="max-h-40 overflow-y-auto mb-3">
                    {workout.exercises.map((exercise, index) => (
                      <div key={exercise.id} className="text-sm mb-2">
                        <div className="font-medium">{exercise.name}</div>
                        <div className="text-gray-400">
                          {exercise.sets} séries × {exercise.reps} repetições • {exercise.weight} kg
                          {exercise.restTime && ` • ${exercise.restTime}s descanso`}
                        </div>
                      </div>
                    ))}
                  </div>

                  {workout.notes && (
                    <div className="text-xs text-gray-400 mt-2 border-t border-gray-700 pt-2">
                      {workout.notes}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </>
      ) : (
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl">{editMode ? 'Editar Treino' : 'Criar Treino'}</h2>
            <button
              onClick={() => {
                resetForm();
                setShowForm(false);
              }}
              className="text-gray-400 hover:text-white"
            >
              Cancelar
            </button>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Nome do Treino</label>
            <input
              type="text"
              value={workoutName}
              onChange={(e) => setWorkoutName(e.target.value)}
              className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="ex: Superior, Dia de Pernas, Corpo Completo, etc."
            />
          </div>

          <div className="mb-4">
            <div className="flex justify-between items-center mb-2">
              <label className="block text-sm font-medium">Exercícios</label>
              <button
                onClick={() => startTimer(60)}
                className="text-sm text-blue-500 hover:text-blue-400"
              >
                Iniciar Temporizador de Descanso
              </button>
            </div>

            <div className="space-y-3 max-h-80 overflow-y-auto mb-3">
              {exercises.map((exercise, index) => (
                <div key={exercise.id} className="bg-gray-700 rounded p-3 relative">
                  <button
                    onClick={() => handleRemoveExercise(exercise.id)}
                    className="absolute top-2 right-2 text-red-500 hover:text-red-400"
                  >
                    <TrashIcon size={16} />
                  </button>
                  
                  <div className="font-medium">{exercise.name}</div>
                  <div className="text-sm text-gray-300 mt-1">
                    {exercise.sets} séries × {exercise.reps} repetições
                  </div>
                  <div className="text-sm text-gray-400">
                    {exercise.weight} kg • {exercise.restTime}s descanso
                  </div>
                  {exercise.notes && (
                    <div className="text-xs text-gray-400 mt-1">
                      Obs: {exercise.notes}
                    </div>
                  )}
                </div>
              ))}

              {exercises.length === 0 && (
                <div className="text-center py-6 text-gray-400 border border-dashed border-gray-700 rounded">
                  Nenhum exercício adicionado ainda
                </div>
              )}
            </div>

            <ExerciseForm onAddExercise={handleAddExercise} />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Observações (Opcional)</label>
            <textarea
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              rows="3"
              placeholder="Quaisquer observações adicionais para este treino"
            ></textarea>
          </div>

          <button
            onClick={handleSaveWorkout}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center justify-center"
          >
            <SaveIcon size={18} className="mr-1" />
            {editMode ? 'Atualizar Treino' : 'Salvar Treino'}
          </button>
        </div>
      )}

      {showTimer && (
        <TimerModal
          duration={timerDuration}
          onClose={() => setShowTimer(false)}
        />
      )}
    </div>
  );
};

export default WorkoutPlanner;
