import React, { useState, useEffect } from 'react';
import { XIcon } from 'lucide-react';

const TimerModal = ({ duration = 60, onClose }) => {
  const [timeLeft, setTimeLeft] = useState(duration);
  const [isPaused, setIsPaused] = useState(false);
  
  useEffect(() => {
    if (isPaused) return;
    
    if (timeLeft <= 0) {
      onClose();
      return;
    }
    
    const timerId = setTimeout(() => {
      setTimeLeft(timeLeft - 1);
    }, 1000);
    
    return () => clearTimeout(timerId);
  }, [timeLeft, isPaused, onClose]);
  
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;
  
  const progress = (1 - timeLeft / duration) * 100;
  
  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-70 z-50">
      <div className="bg-gray-800 rounded-lg p-5 w-11/12 max-w-sm relative">
        <button 
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-400 hover:text-white"
        >
          <XIcon size={20} />
        </button>
        
        <h3 className="text-xl font-bold mb-4 text-center">Temporizador de Descanso</h3>
        
        <div className="flex justify-center mb-4">
          <div className="text-4xl font-mono">
            {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}
          </div>
        </div>
        
        <div className="w-full bg-gray-700 rounded-full h-2.5 mb-4">
          <div 
            className="bg-blue-600 h-2.5 rounded-full" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
        
        <div className="flex justify-center space-x-3">
          <button
            onClick={() => setIsPaused(!isPaused)}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            {isPaused ? 'Continuar' : 'Pausar'}
          </button>
          <button
            onClick={onClose}
            className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded"
          >
            Finalizar
          </button>
        </div>
      </div>
    </div>
  );
};

export default TimerModal;
